import { ethers } from "ethers";
import "./App.css";

function App() {

  let signer = null;
  let provider;

  if (window.ethereum == null) {
    console.log("MetaMask not installed; using read-only defaults")
    provider = ethers.getDefaultProvider()
  } else {
    // Request access to read-only op
    provider = new ethers.BrowserProvider(window.ethereum)

    // Request access to write operations, which will be performed by
    // the private key that MetaMask manages.
    signer = provider.getSigner().then((signer) => { signer = signer })
